import React, {Component} from 'react'
import NewsItem from './NewsItem';

export default class News extends Component {

  constructor() {
    super();
    this.state = {
      articles: [],
      loading: false
    }
  }

  async componentDidMount(){
    let url= "https://newsapi.org/v2/top-headlines?country=in&country=&apiKey=3f8d773dd9d041959eb5059c4bf52a36"
    let data = await fetch(url);
    let parsedData = await data.json()
    this.setState({articles: parsedData.articles})

  }
  render() {
    return (
      <div className="container my-3">
        <h2 className="my-2">News Network - Top Headlines</h2>
        <div className="row">
          {this.state.articles.map((element) => {
            return <div className="col-md-4" key={element.url}>
              <NewsItem
                title={element.title ? element.title.slice(0,80) : ""}
                description={element.description ? element.description.slice(0,79): ""}
                imgUrl={element.urlToImage || ""}
                newsUrl={element.url || ""}
              /> 
            </div>;
          })}
        </div>
      </div>
    );
  }
}
