import React, {Component} from 'react'
import NewsItem from './NewsItem';

export default class News extends Component {

  constructor() {
    super();
    this.state = {
      articles: [],
      loading: false,
      page:1

    }
  }

  async componentDidMount(){
    let url= "`https://newsapi.org/v2/everything?domains=wsj.com&apiKey=3f8d773dd9d041959eb5059c4bf52a36&pagesize=20`"
    let data = await fetch(url);
    let parsedData = await data.json()
    this.setState({articles: parsedData.articles, totalArticles: parsedData.totalResults})

  }

  handlePrevClick =async () => {
    let url= `https://newsapi.org/v2/everything?domains=wsj.com&apiKey=3f8d773dd9d041959eb5059c4bf52a36&page=${this.state.page - 1}&pagesize=20`;
    let data = await fetch(url);
    let parsedData = await data.json()
  this.setState({
    page: this.state.page -1,
    articles: parsedData.articles
  })
 }

  handleNextClick = async () => {
  if(this.state.page +1 > Math.ceil(this.state.totalResults/20)){

  }
  else 
  {
    let url= `https://newsapi.org/v2/everything?domains=wsj.com&apiKey=3f8d773dd9d041959eb5059c4bf52a36&page=${this.state.page + 1}&pagesize=20`;
    let data = await fetch(url);
    let parsedData = await data.json()
    this.setState
    ({
      page: this.state.page +1,
      articles: parsedData.articles
    })
  }
}


  render() {
    return (
      <div className="container my-3">
      <h1 className="text-center">
        <h2  id="header">News Network - Top Headlines</h2>
        </h1>
        <div className="row">
          {this.state.articles.map((element) => {
            return <div className="col-md-4" key={element.url}>
              <NewsItem
                title={element.title ? element.title.slice(0,80) : ""}
                description={element.description ? element.description.slice(0,79): ""}
                imgUrl={element.urlToImage || ""}
                newsUrl={element.url || ""}
              /> 
            </div>;
          })}
        </div>
        <div className="container d-flex justify-content-between">
          <button disabled={this.state.page<=1} type="button" className="btn btn-dark" onClick={this.handlePrevClick}>&larr; Previous Page</button>
          <button disabled={this.state.page +1 > Math.ceil(this.state.totalResults/20)} type="button" className="btn btn-dark" onClick={this.handleNextClick}>Next Page &rarr;</button>
        </div>
      </div>
    );
  }
}
